################################################## Left-to-Right Binary Method

## Calculate ME with LR Binary method
def LR_BinaryM(x,k,p):
    k = list(bin(k)[2:])
    x1 = 1
    for i in range(len(k)-1 ,-1,-1):
        x1  = x1**2 % p
        if int(k[i]) == 1:
            x1 = x1 * x % p
    return x1

################################################## Window Method

## List of the digits in the base 'b' representation of n 
def Win_Rep(k, base):
    digits = []
    while k:
        digits.append(k % base)
        k //= base
    return digits

## Calculate ME with Window method
def ME_Window(x,k,w,p):
    base = 2 << (w - 1)

    # Precompute the table of exponents

    table = [1] * base
    for i in range(1, base):
        table[i] = table[i - 1] * x % p
    r = 1
    for digit in reversed(Win_Rep(k, base)):
        for i in range(w):
            r = r * r % p
        if digit:
            r = r * table[digit] % p

    return r




################################################## NAF Method

## NAF reoresentation of k
def naf(k):
    Naf = []
    i=0
    while k>0:
        if k%2 == 1:
            Naf.append(2 - k%4)
            k = k - Naf[i]
        else:
            Naf.append(0)
        k = k//2
        i = i+1
    return Naf

# Calculate ME with WNAF method
def ME_NAF(m,k, p):
    Naf=naf(k)
        
    x = m
    y = inverse_mod_Euclid(m, p)
    for j in range(len(Naf)-2,-1,-1):
        x = x*x % p
        if Naf[j] == 1:
            x = x*m % p
        if Naf[j] == -1:
            x = x*y % p
            
    return x

############################################# WNAF method

def mods(d,w):
    t=pow(2,w)
    if (d % t) >= t - 1:
        return (d % t) - t
    else:
        return d % t

# WNAF representation of k    

def w_naf(k,w):
    wNaf=[]
    wNafT=[]
    i=0
    while k>0:
        if k%2 == 1:
            wNaf.append(mods(k,w))
            k = k - wNaf[i]
        else:
            wNaf.append(0)
        k = k//2
        i = i+1
    for i in wNaf:
        wNafT.append(abs(i))
        
    wNafT = set(wNafT)
    return (wNaf,list(set(wNafT)))

# Calculate ME with WNAF method
def ME_WNAF(x,k,w,p):
        wNaf = w_naf(k,w)[0]
        wNafT = w_naf(k,w)[1]
        R={}
        R1={}
        for l in wNafT:
            b = (x**l) % p
            R[l] = b
            R1[l] = inverse_mod_Euclid(b,p)
        exp = 1
        for j in range(len(wNaf)-1,-1,-1):
            exp = (exp*exp) % p
            if wNaf[j] > 0:
                exp = (exp*R[wNaf[j]]) % p
            if wNaf[j] < 0:
                exp = (exp * R1[-wNaf[j]]) % p
            
        return exp

############################################# ZR method

## Alternate-Zeckendorf Representation of an integer k
def ZechendorfRep(k):
    F = [1,2,3]
    ZR =[]
    l = len(bin(k)[2:]) +1
    for i in range(3,l):
        F.append(F[i-1]+2*F[i-2])
    for j in range(l-1,-1,-1):
        if (k - F[j] >= 0):
            ZR.append(1)
            k = k - F[j]
        else:
            ZR.append(0)
    return ZR[::-1],l

## Calculate ME with Zechendorf method
def ME_ZR(x,k,n):
    ZR,l = ZechendorfRep(k)
    U = x
    V = x**2 % n
    W = U*V %n
    temp = 1
    z = U**ZR[0] * V**(ZR[1]) * W**(ZR[2]) %n
    for i in range(3,l):
        temp = V
        V = W
        W = temp**2 * W %n
        if ZR[i] == 1:
            z = z*W % n
    return z


